mapper.update:
UPDATE PLACE
SET STEP = STEP-1
WHERE ONEDAY_SEQ = #{oneday_seq} AND PLACE_SEQ = #{place_seq}

UPDATE PLACE
SET STEP = STEP+1
WHERE  ONEDAY_SEQ = #{oneday_seq} AND PLACE_SEQ = #{place_seq}

UPDATE PLACE
SET STEP = STEP+1
WHERE ONEDAY_SEQ = #{oneday_seq} AND PLACE_SEQ = #{place_seq}

UPDATE PLACE 
SET STEP = STEP-1
WHERE ONEDAY_SEQ = #{oneday_seq} AND PLACE_SEQ = #{place_seq}

UPDATE PLACE
SET MEMO=#{memo}
WHERE PLACE_SEQ=#{place_seq}
