mapper.update:
UPDATE REVIEWBOARD
SET UUID_NAME=#{uuid_name} , ORIGIN_NAME=#{origin_name}, IMG_URL=#{img_url}, REVIEW_TITLE=#{review_title}, CONTENT=#{content}
WHERE PLACE_SEQ=#{place_seq}

UPDATE TOGETHER.REVIEWBOARD
SET UUID_NAME=NULL , ORIGIN_NAME=NULL, IMG_URL=NULL, REVIEW_TITLE=NULL, CONTENT=NULL 
WHERE PLACE_SEQ=#{place_seq}
